  return (
    <button className="button" onClick={onClick}>
      {children}
    </button>
  );
}

function App() {
  return (
    <h1 className="app">
      <div className="sidebar">
        <FriendList />
        <FormAddFriend />
        <Button>Add Friend</Button>
      </div>
      <FormSplit />
    </h1>
  );
}

