import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/150?img=29",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
  {
    id: 499996,
    name: "jim",
    image: "https://i.pravatar.cc/150?img=68",
    balance: 100,
  },
];

function Button({ children, onClick }) {
  return (
    <button className="button" onClick={onClick}>
      {children}
    </button>
  );
}

function App() {
  const [displayAddFriendForm, setDisplayAddFriendForm] = useState(false);

  function handleDisplayAddFriendForm() {
    setDisplayAddFriendForm((status) => !status);
  }

  return (
    <h1 className="app">
      <div className="sidebar">
        <FriendList />
        {displayAddFriendForm && <FormAddFriend />}
        <Button onClick={handleDisplayAddFriendForm}>
          {displayAddFriendForm ? "Close" : "Add Friend"}
        </Button>
      </div>
      <FormSplit />
    </h1>
  );
}

function FriendList() {
  const friends = initialFriends;
  return (
    <ul>
      {friends.map((friend) => (
        <Friend friend={friend} key={friend.id} />
      ))}
    </ul>
  );
}

function Friend({ friend }) {
  return (
    <li>
      <img src={friend.image} alt={friend.name} />

      <label>{friend.name}</label>

      {friend.balance > 0 && (
        <p className="green">
          {friend.name} owes you ${friend.balance}
        </p>
      )}
      {friend.balance < 0 && (
        <p className="red">
          you owes {friend.name} ${friend.balance}
        </p>
      )}
      {friend.balance === 0 && <p>you and {friend.name} are even</p>}

      <Button>select</Button>
    </li>
  );
}

function FormAddFriend() {
  const [name, setName] = useState("");
  const [url, setUrl] = useState("");
  return (
    <form className="form-add-friend">
      <label>üíÅ‚Äç‚ôÇÔ∏è Name</label>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />

      <label>üñº Image</label>
      <input type="text" />
    </form>
  );
}

function FormSplit() {
  return (
    <form className="form-split-bill">
      <h2>split a bill with X</h2>

      <label>üíµ bill value</label>
      <input type="text" />

      <label>üë®‚Äçü¶± your expense</label>
      <input type="text" />

      <label>üôã‚Äç‚ôÇÔ∏è X's expense</label>
      <input type="text" />

      <label>ü§ë who's paying the bill</label>
      <select>
        <option value="user">you</option>
        <option value="friend">X</option>
      </select>

      <Button>split bill</Button>
    </form>
  );
}

export default App;
